import React from 'react';

interface Props {
  actions: any[];
  scores: any[];
  gameStates: any[];
  duration: number; // total frames
  onSeek: (sec: number) => void;
}

const actionIcon = (action?: string) => {
  switch (action) {
    case 'spike':
      return 'üí•';
    case 'set':
      return 'üëê';
    case 'receive':
      return 'üõ°Ô∏è';
    case 'serve':
      return 'üéØ';
    case 'block':
      return 'üß±';
    default:
      return '‚Ä¢';
  }
}

export const EventTimeline: React.FC<Props> = ({ actions = [], scores = [], gameStates = [], duration, onSeek }) => (
  <div className="relative w-full h-16 mt-3">
    {/* ÁãÄÊÖãËâ≤Ê¢ù */}
    <div className="absolute w-full h-2 rounded bg-gray-200">
      {gameStates?.map((s: any, i: number) => (
        <div
          key={i}
          style={{
            left: `${(s.start_frame / duration) * 100}%`,
            width: `${((s.end_frame - s.start_frame) / duration) * 100}%`,
            background: s.state === 'Play' ? '#86efac' : s.state === 'No-Play' ? '#fecaca' : '#fde68a'
          }}
          className="absolute h-full rounded"
          title={`${s.state}`}
        />
      ))}
    </div>

    {/* ÂæóÂàÜ */}
    {scores?.map((e: any, i: number) => (
      <button
        key={`score-${i}`}
        style={{ left: `${(e.frame / duration) * 100}%` }}
        className="absolute top-0 -translate-x-1/2 rounded-full bg-white border shadow px-2 text-sm hover:shadow-md"
        onClick={() => onSeek(e.timestamp)}
        title={`#${e.player_id} ÂæóÂàÜ @ ${e.timestamp?.toFixed(1)}s`}
      >üèê</button>
    ))}

    {/* Ë°åÁÇ∫ */}
    {actions?.map((a: any, i: number) => (
      <button
        key={`act-${i}`}
        style={{ left: `${(a.frame / duration) * 100}%` }}
        className="absolute top-7 -translate-x-1/2 text-xs text-blue-700 hover:text-blue-900"
        onClick={() => onSeek(a.timestamp)}
        title={`#${a.player_id} ${a.action} @ ${a.timestamp?.toFixed(1)}s`}
      >{actionIcon(a.action)}</button>
    ))}
  </div>
);
