import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { getVideos } from '../services/api';

export const Dashboard: React.FC = () => {
  const [videos, setVideos] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    getVideos()
      .then((res) => setVideos(res.videos || []))
      .finally(() => setLoading(false));
  }, []);

  return (
    <div className="space-y-8">
      <section>
        <h1 className="text-2xl md:text-3xl font-bold">儀表板</h1>
        <p className="text-gray-600 mt-2">快速概覽已上傳影片與分析狀態</p>
      </section>

      <section className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        {loading && <div className="text-gray-500">載入中...</div>}
        {!loading && videos.length === 0 && (
          <div className="text-gray-500">尚未有上傳影片，請前往「上傳」開始分析</div>
        )}
        {videos.map((v) => (
          <div key={v.id} className="bg-white rounded-xl shadow p-5 flex flex-col gap-3">
            <div className="flex items-center justify-between">
              <div className="font-semibold truncate" title={v.filename}>{v.filename}</div>
              <span className={`text-xs px-2 py-1 rounded-full border ${
                v.status === 'completed' ? 'bg-green-50 text-green-700 border-green-200' :
                v.status === 'processing' ? 'bg-blue-50 text-blue-700 border-blue-200' :
                v.status === 'failed' ? 'bg-red-50 text-red-700 border-red-200' : 'bg-gray-50 text-gray-700 border-gray-200'
              }`}>
                {v.status}
              </span>
            </div>
            <div className="text-sm text-gray-500">上傳時間：{new Date(v.upload_time).toLocaleString()}</div>
            <div className="flex gap-3 mt-2">
              <Link to={`/player/${v.id}`} className="px-3 py-2 rounded bg-blue-600 text-white text-sm hover:bg-blue-700 transition">播放/檢視</Link>
              <Link to="/library" className="px-3 py-2 rounded border text-sm hover:bg-white">前往影片庫</Link>
            </div>
          </div>
        ))}
      </section>
    </div>
  );
}
